// let resultado = (condicao) ? (retorno se verdadeiro) : (retorno se falso)

// if-else convencional
let preco = 100;
if (preco < 40) {
  console.log("Livro barato!");
} else {
  console.log("Livro não é barato!");
}

// operador ternario
let resposta = preco < 40 ? "Livro barato!" : "Livro não é barato!";
console.log(resposta);
